draw(A--C);
draw(B--D);
draw(P--S);
draw(A--F);
draw(P--R);
draw(P--Q);

label("$A$",A,NW);
label("$B$",B,NE);
label("$C$",C,SE);
label("$D$",D,SW);
label("$P$",P,N);
label("$S$",S,SE);
label("$T$",T,N);
label("$E$",X,SW+SE);
label("$R$",R,SW);
label("$F$",F,SE);
label("$Q$",Q,SW);
