draw(dir(50) -- dir(90));
label("$a$", dir(50) -- dir(90), N);
draw(dir(10) -- dir(90));
label("$b$", dir(10) -- dir(90), SW);
draw(dir(-70) -- dir(90));
label("$d$", dir(-70) -- dir(90), E);
