\begin{asy}
import olympiad;
import cse5;
defaultpen(white);
size(100);
defaultpen(linewidth(.8pt));
dotfactor=4;

pair A = origin;
pair B = (1.25,1);
pair C = (2,0);
pair D = midpoint(A--C);
pair E = midpoint(A--B);
