\\
\\
$\textbf{(A)}$\\

\begin{asy}
import olympiad;
import cse5;
defaultpen(white);
size(100);

size(75);
pair A= (1.5,1) ,
B= (3,3) ,
C= (4.5,6) ,
D= (6,10) ,
E= (7.5,15) ;
draw((0,-1)--(0,16));
draw((-1,0)--(16,0));
dot(A^^B^^C^^D^^E);

label("$A$", (0,8), W);
label("$C$", (8,0), S);
\end{asy}
