        draw(rotate(180,(2,2))*arc((1,3),1, 90, -180));
        draw(rotate(270,(2,2))*arc((1,3),1, 90, -180));
        draw((0,1)--(0,0)--(1,0));
        draw(rotate(270,(2,2))*((0,1)--(0,0)--(1,0)));
        draw(rotate(180,(2,2))*((0,1)--(0,0)--(1,0)));
        draw(rotate(90,(2,2))*((0,1)--(0,0)--(1,0)));    
